import React from 'react';
import classes from './Movies.module.css';

const Movies = (
  {
    backdrop_path,
    original_title,
    original_language,
    release_date,
    vote_average,
    vote_count,
  },
  props,
) => {
  return (
    // movie box
    <div className={classes.moviesBox}>
      {/* movie image  */}
      <div className={classes.moviesImage}>
        <div className={classes.topInfo}>
          <b>Rating: {vote_average}</b>
        </div>
        <img
          src={`https://www.themoviedb.org/t/p/w440_and_h660_face/${backdrop_path}`}
          alt={original_title}
        />{' '}
      </div>
      {/* movie texts section */}
      <div className={classes.textPart}>
        <div className={classes.moviesTitle}>
          <h3> {original_title}</h3>
        </div>
        <div className={classes.moviesOtherInfo}>
          Language: {original_language}
        </div>
        <div className={classes.moviesVotes}>
          {' '}
          {/* Rating: {vote_average} | Vote Count: {vote_count} */}
        </div>
        <span className={classes.releaseDate}>
          {' '}
          Release Date: {release_date}
        </span>
        <div className={classes.likeButtonBox}>
          {' '}
          <button onClick={props.onClick}>Like</button>
        </div>
      </div>
    </div>
  );
};

export default Movies;
