import React, {useEffect, useState} from 'react';
import ReactDOM from 'react-dom';
import {BrowserRouter, Route} from 'react-router-dom';

function App() {
  const moviesApi =
    'https://api.themoviedb.org/3/movie/popular?api_key=d0f5f2e135336200362af8a1a73acb17';
  const [movies, setMovies] = useState([]);

  //use effect to call the API
  useEffect(() => {
    fetch(moviesApi)
      .then(response => response.json())
      .then(data => setMovies(data.results));
  }, []);

  const allMovies = () => {
    movies.map(movies => <p>{movies.original_title}</p>);
  };

  const likedMovies = () => {
    <h1> MOVIES I LIKED</h1>;
  };

  return (
    <BrowserRouter>
      <div>
        <Route path="/" components={allMovies} />
        <Route path="/liked" components={likedMovies} />
      </div>
    </BrowserRouter>
  );
}

export default App;
