import React from 'react';
import classes from './Showcase.module.css';
import $ from 'jquery';
const Showcase = props => {
  const slideshow = [
    require('../../assets/image/s-1.jpg'),
    require('../../assets/image/s-2.jpg'),
    require('../../assets/image/s-3.jpg'),
    require('../../assets/image/s-4.jpg'),
    require('../../assets/image/s-5.png'),
  ];
  let alphabet = 'a';
  return (
    <section id="main">
      <h1 className={classes.showcaseHeading}>Showcase</h1>
      <ul id="autoWidth" className="cs-hidden">
        {slideshow.map((slide, i) => (
          <li className={`item-${alphabet}`} key={i}>
            <div className={classes.showcaseBox}>
              <img src={slide} alt="showcase movie" />
            </div>

            <span style={{display: 'none'}}>
              {(alphabet = String.fromCharCode(alphabet.charCodeAt(0) + 1))}
            </span>
          </li>
        ))}
      </ul>
      $(document).ready(function (){' '}
      {$('#autoWidth').lightSlider({
        autoWidth: true,
        loop: true,
        onSliderLoad: function () {
          $('#autoWidth').removeClass('cS-hidden');
        },
      })}
      );
    </section>
  );
};

export default Showcase;
