import React, {useEffect, useState} from 'react';
import {BrowserRouter as Router, Routes, Route} from 'react-router-dom';
import LikedMovies from './components/likedMovies/LikedMovies';
import Movies from './components/movies/Movies';

function App() {
  const moviesApi =
    'https://api.themoviedb.org/3/movie/popular?api_key=d0f5f2e135336200362af8a1a73acb17';
  const [movies, setMovies] = useState([]);

  //use effect to call the API
  useEffect(() => {
    fetch(moviesApi)
      .then(response => response.json())
      .then(data => setMovies(data.results));
  }, []);

  const moviesList = movies.map(movies => <p>{movies.original_title}</p>);

  return (
    <Router>
      <Routes>
        <Route path="/" components={<Movies />} />
        <Route path="/liked" element={<LikedMovies />} />
      </Routes>
    </Router>
  );
}

export default App;
