import React, {useEffect, useState} from 'react';
import {BrowserRouter as Router, Routes, Route} from 'react-router-dom';
import LikedMovies from './components/likedMovies/LikedMovies';
import Movies from './components/movies/Movies';
import MoviesRoute from './components/MoviesRoute';
import Navigation from './components/nav/Navigation';
function App() {
  const moviesApi =
    'https://api.themoviedb.org/3/movie/popular?api_key=d0f5f2e135336200362af8a1a73acb17';
  const [movies, setMovies] = useState([]);

  //use effect to call the API
  useEffect(() => {
    fetch(moviesApi)
      .then(response => response.json())
      .then(data => setMovies(data.results));
  }, []);

  const allMovies = () => {
    movies.map(movies => <Movies {...movies} key={movies.id} />);
  };
  return (
    <div className="App">
      <Navigation />
      {/* -------------- Beginning of router ----------------- */}
      <Router>
        <Routes>
          <Route
            path="/"
            element={
              <MoviesRoute
                allMovie={movies.map(movies => (
                  <Movies {...movies} key={movies.id} />
                ))}
              />
            }
          />
          <Route path="/liked" element={<LikedMovies />} />
        </Routes>
      </Router>
      {/* -------------- End of Router ---------------- */}
    </div>
  );
}

export default App;
