import React from 'react';
import classes from './Movies.module.css';

const Movies = ({
  backdrop_path,
  original_title,
  overview,
  popularity,
  release_date,
  vote_average,
  vote_count,
}) => {
  return (
    // movie box
    <div className={classes.moviesBox}>
      {/* movie image  */}
      <div className={classes.moviesImage}>
        {' '}
        <img
          src={`https://www.themoviedb.org/t/p/w440_and_h660_face/${backdrop_path}`}
          alt={original_title}
        />{' '}
      </div>
      {/* movie texts section */}
      <div className={classes.moviesTitle}>
        <h3>
          {' '}
          {original_title} | Release Date: {release_date}
        </h3>
      </div>
      <div className={classes.moviesOverview}>Overview: {overview}</div>

      <div className={classes.moviesOtherInfo}>Popularity: {popularity}</div>
      <div className={classes.moviesVotes}>
        {' '}
        Rating: {vote_average} | Vote Count: {vote_count}
      </div>
    </div>
  );
};

export default Movies;
