import React, {useState} from 'react';
import Showcase from './showcase/Showcase';
import LikedMovies from './likedMovies/LikedMovies';
import classes from './LikedMoviesRoute';

const LikedMoviesRoute = props => {
  const [likedMovies, useLikedMovies] = useState([]);

  const moviesLike = () => {
    props.getData();
  };
  useLikedMovies(moviesLike);
  //use effect to call the API
  //   useEffect(() => {
  //     const
  //  }, []);

  return (
    <>
      <Showcase />
      {likedMovies.length === 0 ? (
        <>
          <div className={classes.moviesHeading}>
            <h2> Latest Movies</h2>
          </div>
          <section className={classes.movieList}>
            {likedMovies.movies.map(movies => (
              <LikedMovies {...movies} key={movies.id} />
            ))}
          </section>
        </>
      ) : (
        <>
          <p> NO MOVIE ADDED YET</p>
        </>
      )}
    </>
  );
};
export default LikedMoviesRoute;
