import React from 'react';
import classes from './Movies.module.css';
import Button from '../UI/Button';

const Movies = (
  {
    backdrop_path,
    original_title,
    original_language,
    release_date,
    vote_average,
  },
  props,
) => {
  const likedMovieData = {
    title: original_title,
    language: original_language,
    releaseDate: release_date,
    imgSource: `https://www.themoviedb.org/t/p/w440_and_h660_face/${backdrop_path}`,
  };

  const addLikedMovie = () => {
    props.likedMovie(likedMovieData);
  };

  return (
    // movie box
    <div className={classes.moviesBox}>
      {/* movie image  */}
      <div className={classes.moviesImage}>
        <div className={classes.topInfo}>Rating: {vote_average}</div>
        <img
          src={`https://www.themoviedb.org/t/p/w440_and_h660_face/${backdrop_path}`}
          alt={original_title}
        />{' '}
      </div>
      {/* movie texts section */}
      <div className={classes.textPart}>
        <div className={classes.moviesTitle}>
          <h3> {original_title}</h3>
        </div>
        <div className={classes.moviesOtherInfo}>
          <b>Language: {original_language.toUpperCase()}</b>
        </div>
        <span className={classes.releaseDate}>
          {' '}
          Release Date: {release_date}
        </span>
        <div className={classes.likeButtonBox}>
          {' '}
          <button className={classes.button} onClick={addLikedMovie}></button>
          {/* <Button className={classes.button} onClick={addLikedMovie}>
            Like
          </Button> */}
          {/* <button onClick={console.log(original_title)}>Like</button> */}
        </div>
      </div>
    </div>
  );
};

export default Movies;
